import {useState} from 'react';

import { createAuthUserWithEmailAndPassword ,createUserDocumentFromAuth } from '../../utils/firebase/firebase.utils';
import FormInput from '../form-input/form-input.componentFormInput'

const defaultFormFields={
    displayName:'',
    email:'',
    password:'',
    confirmPassword:'',

}

const SignUpForm=()=>{

    const [formFields,setFormFields] =useState(defaultFormFields);
    const {displayName,email,password,confirmPassword}=formFields;

    console.log(formFields);

    const resetFormFields =()=>{
        setFormFields(defaultFormFields);
    }

    const handleSubmit =async(event)=>{
        event.preventDefault();
        if(password!=confirmPassword){
            alert('password dont match');
            return;
        }
        try{
            const {user} =await createAuthUserWithEmailAndPassword(email,password);

            await createUserDocumentFromAuth(user,{displayName}); 
            resetFormFields();
        }
        catch(error){
            if(error.code=='auth/email-already-in-use')
                alert('email already in use');
            console.log(error);
            console.log("user creation encountered an error ",error);
        }
    }

    const handleChange = (event)=>{
        const {name,value}=event.target;
        setFormFields({...formFields,[name]:value});

    }

    return (
        <div>
            <h1>Sign up with your email and password !</h1>

            <form onSubmit={handleSubmit} >
                <label htmlFor="">Display Name</label>
                <FormInput label="" type="text" onChange={handleChange} name='displayName' value={displayName} required   />
                <label htmlFor="">Email</label>
                <FormInput type="email" onChange={handleChange} name='email' value={email}  required />
                <label htmlFor="">Password</label>
                <FormInput type="password" onChange={handleChange} name='password' value={password} required />
                <label htmlFor="">Confirm Password</label>
                <FormInput type="password" onChange={handleChange}  name='confirmPassword' value={confirmPassword} required />

                <button type="submit">Sign Up</button>
            </form>
        </div>
    )
}
export default SignUpForm;
